	[248] = {"248", "Коллективный разум", "Синие пауки и мухи наносят двойной урон#Паучьи и мушиные фамильяры становятся сильнее"}, -- Hive Mind
	[253] = {"253", "Магическая короста", "↑ {{Heart}} +1 к здоровью#↑ {{Luck}} +1 к удаче#{{HealingRed}} Лечит 1 сердце"}, -- Magic Scab
	[254] = {"254", "Кровяной сгусток", "↑ {{Damage}} +1 к урону для левого глаза#↑ {{Range}} +2.75 к дальности для левого глаза"}, -- Blood Clot
	[256] = {"256", "Горячие бомбы", "{{Bomb}} +5 бомб#{{Burning}} Бомбы Исаака наносят контактный урон#{{Burning}} Бомбы Исаака оставляют пламя там, где взрываются#{{Burning}} Иммунитет к огню (кроме снарядов)"}, -- Hot Bombs
	[261] = {"261", "Проптоз", "↑ {{Damage}} +0.5 к урону#↓ Слёзы наносят меньше урона, чем дольше они в воздухе#Слёзы наносят 3x урона в упор и не наносят урона через 0.8 секунды"}, -- Proptosis
	[262] = {"262", "Пропавшая страница 2", "{{BlackHeart}} +1 чёрное сердце#Получение урона до 1 сердца наносит урон всем врагам в комнате#{{Collectible35}} Чёрные сердца и эффекты, подобные Некрономикону, наносят двойной урон"}, -- Missing Page 2
	[263] = {"263", "Чистая руна", "{{Rune}} Создаёт 1 руну при подборе#{{Rune}} Активирует эффект руны, которую держит Исаак, не используя её"}, -- Clear Rune (Repentance item)
	[264] = {"264", "Умная муха", "Орбитальный#Атакует врагов, когда Исаак получает урон#Наносит 6.5 контактного урона в секунду"}, -- Smart Fly
	[272] = {"272", "ЛВВ", "Дружелюбная взрывающаяся муха#Взрыв наносит 100 урона#{{Warning}} Взрыв может навредить Исааку"}, -- BBF
	[273] = {"273", "Мозг Боба", "Устремляется в направлении, в котором стреляет Исаак#Взрывается при попадании во врага#{{Poison}} Взрыв наносит 100 урона и отравляет врагов#{{Warning}} Взрыв может навредить Исааку"}, -- Bob's Brain
	[274] = {"274", "Лучший друг", "Получение урона создаёт один орбитальный спутник среднего радиуса на комнату#Он наносит 150 контактного урона в секунду"}, -- Best Bud
	[275] = {"275", "Маленький Бримстоун", "{{Chargeable}} Фамильяр, который заряжается и стреляет {{Collectible118}} кровавым лучом#Он наносит 24 урона за 0.63 секунды"}, -- Lil Brimstone
	[276] = {"276", "Сердце Исаака", "Исаак становится неуязвимым#Создаёт фамильяра-сердце, который следует за Исааком#Сердце заряжается, когда Исаак стреляет, и выпускает очередь слёз, когда он прекращает#{{Warning}} Если фамильяр-сердце получает урон, Исаак получает урон"}, -- Isaac's Heart
	[278] = {"278", "Тёмный попрошайка", "{{Heart}} Подбирает ближайшие красные сердца#Создаёт чёрное сердце, руну, карту, пилюлю или паука за каждые 1.5 подобранных сердца"}, -- Dark Bum
	[280] = {"280", "Длинноногая неженка", "Случайно создаёт синих пауков во враждебных комнатах#{{Charm}} Очаровывает врагов, с которыми вступает в контакт"}, -- Sissy Longlegs
	[283] = {"283", "D100", "Дублирует 1 подбираемый предмет в комнате#Перебрасывает скорость, скорострельность, урон, дальность и пассивные предметы Исаака#Перебрасывает все предметы на пьедесталах, подбираемые предметы и камни в комнате#Перезапускает комнату, возрождает всех врагов и понижает их уровень"}, -- D100
	[285] = {"285", "D10", "Понижает уровень всех врагов в комнате#Например, все красные мухи становятся чёрными"}, -- D10
	[286] = {"286", "Пустая карта", "Активирует эффект карты, которую держит Исаак, не используя её"}, -- Blank Card
	[287] = {"287", "Книга тайн", "Подсвечивает тонированные камни и камни с потайными комнатами в комнате#{{Timer}} Даёт один из этих эффектов на этаж:#{{Collectible54}} Карта сокровищ#{{Collectible21}} Компас#{{Collectible246}} Синяя карта#Даёт только те эффекты, которые ещё не активны#{{Collectible76}} Если все эффекты активны, даёт рентгеновское зрение"}, -- Book of Secrets
	[288] = {"288", "Коробка с пауками", "Создаёт 4-8 синих пауков"}, -- Box of Spiders
	[289] = {"289", "Красная свеча", "Бросает красное пламя#Оно наносит контактный урон, блокирует вражеские слёзы и исчезает после нанесения урона или блокировки 4 выстрелов, или через 10 секунд"}, -- Red Candle
	[291] = {"291", "Смыв!", "Превращает всех не-боссов в какашки#Мгновенно убивает какашечных врагов и боссов#Тушит камины и заполняет комнату водой#Превращает лавовые ямы в проходимую землю"}, -- Flush!
	[292] = {"292", "Сатанинская библия", "{{BlackHeart}} +1 чёрное сердце#{{DevilRoom}} Использование предмета перед боем с боссом заставляет его вознаграждать сделкой с дьяволом#Покупка этих сделок с дьяволом имеет те же последствия, что и в комнатах Дьявола#Не влияет на пьедесталы с предметами на этаже Пустоты"}, -- Satanic Bible
	[293] = {"293", "Голова Крампуса", "{{Collectible118}} Выстреливает 4-сторонним кровавым лучом#Каждый наносит 200 урона за 1.33 секунды"}, -- Head of Krampus
	[294] = {"294", "Бобовая отрыжка", "Отталкивает ближайших врагов и снаряды#Враги, отброшенные в препятствия, получают 10 урона"}, -- Butter Bean
	[295] = {"295", "Волшебные пальцы", "Наносит 2x урона Исаака + 10 всем врагам в комнате#{{Coin}} Стоит 1 монету"}, -- Magic Fingers
	[296] = {"296", "Конвертер", "{{Heart}} Превращает 1 сердце души или чёрное сердце в 1 контейнер сердца"}, -- Converter
	-- ЗАМЕТКА ДЛЯ ЛОКАЛИЗАТОРОВ: Существует код для подсветки текста вашего текущего этажа
	-- Чтобы он работал, используйте только разрывы строк или точки с запятой для разделения деталей этажа и используйте тот же порядок, что и в английском
	[297] = {"297", "Ящик Пандоры", "{{NoLB}}Создаёт награды в зависимости от этажа:#П1: 2{{SoulHeart}}; П2: 2{{Bomb}} + 2{{Key}}#{{NoLB}}Пщ1: Предмет босса; Пщ2: Пщ1 + 2{{SoulHeart}}#Г1: 4{{SoulHeart}}; Г2: 20{{Coin}}#М1: 2 предмета босса#М2: {{Collectible33}} Библия#???/Пустота: Ничего#Преисподняя: Предмет дьявола + 1{{BlackHeart}}#Собор: Предмет ангела + 1{{EternalHeart}}#{{NoLB}}Тёмная комната: Открывает {{Collectible523}} Передвижной ящик#Сундук: 1{{Coin}}#Дом: {{Collectible580}} Красный ключ"}, -- Pandora's Box
	[300] = {"300", "Овен", "↑ {{Speed}} +0.25 к скорости#Движение со скоростью выше 0.85 делает Исаака неуязвимым к контактному урону и наносит 25 урона врагам"}, -- Aries
	[307] = {"307", "Козерог", "↑ {{Heart}} +1 к здоровью#↑ {{Speed}} +0.1 к скорости#↑ {{Tears}} +0.5 к скорострельности#↑ {{Damage}} +0.5 к урону#↑ {{Range}} +0.75 к дальности#+1 {{Coin}} монета, {{Bomb}} бомба и {{Key}} ключ#{{HealingRed}} Лечит 1 сердце"}, -- Capricorn
	[308] = {"308", "Водолей", "Исаак оставляет за собой след жижи#{{Damage}} Жижа наносит 66% урона Исаака в секунду и наследует эффекты его слёз"}, -- Aquarius
	[309] =	{"309", "Рыбы", "↑ {{Tears}} +0.5 к скорострельности#↑ {{Tearsize}} +0.12 размер слезы#Увеличивает отбрасывание от слёз"}, -- Pisces
	[310] =	{"310", "Тушь Евы", "↑ {{Damage}} x2 множитель урона#↓ {{Tears}} x0.66 множитель скорострельности#↓ {{Shotspeed}} -0.5 к скорости полёта слёз"}, -- Eve's Mascara
	[314] = {"314", "Громовые ляжки", "↑ {{Heart}} +1 к здоровью#↓ {{Speed}} -0.4 к скорости#{{HealingRed}} Лечит 1 сердце#Исаак может разрушать камни, врезаясь в них"}, -- Thunder Thighs
	[315] = {"315", "Странный аттрактор", "Слёзы Исаака притягивают врагов, подбираемые предметы и аксессуары#Эффект притяжения намного сильнее в конце пути слёз"}, -- Strange Attractor
	[316] = {"316", "Проклятый глаз", "Заряженная волна из 5 слёз#{{Warning}} Получение урона при частичном заряде телепортирует Исаака в случайную комнату"}, -- Cursed Eye
	[319] = {"319", "Другой глаз Каина", "Стреляет слезами в случайных направлениях с теми же эффектами, что и у Исаака#{{Damage}} Наносит 75% урона Исаака"}, -- Cain's Other Eye
	[320] = {"320", "Единственный друг ???", "Управляемая муха#Наносит 15 контактного урона в секунду"}, -- ???'s Only Friend
	[323] = {"323", "Слёзы Исаака", "Выстреливает 8 слезами во всех направлениях#Слёзы копируют эффекты слёз Исаака, плюс 5 урона#Перезаряжается при стрельбе слезами"}, -- Isaac's Tears
	[325] = {"325", "Ножницы", "{{Timer}} Голова Исаака превращается в стационарного фамильяра на комнату#Тело управляется отдельно и извергает кровавые слёзы в направлении, в котором стреляет Исаак"}, -- Scissors
	[326] = {"326", "Дыхание жизни", "Удержание кнопки USE опустошает полосу заряда#Исаак временно неуязвим, когда полоса заряда пуста#Исаак вызывает лучи света при контакте с врагами в неуязвимом состоянии#Если урон блокируется с идеальной точностью, выстреливает 4-сторонним святым лучом и даёт кратковременный щит#{{Warning}} Слишком долгое удержание наносит урон Исааку"}, -- Breath of Life
	[328] = {"328", "Негатив", "↑ {{Damage}} +1 к урону#Получение урона при половине красного сердца или без него наносит урон всем врагам в комнате"}, -- The Negative
	[330] = {"330", "Соевое молоко", "↑ {{Tears}} x5.5 множитель скорострельности#↓ {{Damage}} x0.2 множитель урона#↓ {{Tearsize}} -0.3 размер слезы#Значительно уменьшает отбрасывание"}, -- Soy Milk
	[331] = {"331", "Божество", "↑ {{Damage}} +0.5 к урону#↓ {{Tears}} -0.3 к скорострельности#↓ {{Shotspeed}} -0.3 к скорости полёта слёз#Самонаводящиеся слёзы#Слёзы получают ауру, которая наносит 60 урона в секунду"}, -- Godhead
	[336] = {"336", "Мёртвый лук", "↑ {{Tearsize}} +0.22 размер слезы#↓ {{Range}} -1.5 к дальности#↓ {{Shotspeed}} -0.4 к скорости полёта слёз#Пронзающие + призрачные слёзы"}, -- Dead Onion
	[339] = {"339", "Английская булавка", "↑ {{Range}} +2.5 к дальности#↑ {{Shotspeed}} +0.16 к скорости полёта слёз#{{BlackHeart}} +1 чёрное сердце"}, -- Safety Pin
	[342] = {"342", "Синяя шапка", "↑ {{Heart}} +1 к здоровью#↑ {{Tears}} +0.7 к скорострельности#↓ {{Shotspeed}} -0.16 к скорости полёта слёз#{{HealingRed}} Лечит 1 сердце"}, -- Blue Cap
	[344] = {"344", "Коробок спичек", "{{BlackHeart}} +1 чёрное сердце#{{Bomb}} Создаёт 3 бомбы#{{Trinket41}} Создаёт Спичку"}, -- Match Book
	[345] = {"345", "Синтоил", "↑ {{Damage}} +1 к урону#↑ {{Range}} +2.5 к дальности"}, -- Synthoil
	[346] = {"346", "Перекус", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Лечит 1 сердце"}, -- A Snack
	[349] = {"349", "Деревянный никель", "{{Coin}} 59% шанс создать случайную монету"}, -- Wooden Nickel
	[352] = {"352", "Стеклянная пушка", "{{Damage}} Выстреливает большой пронзающей призрачной слезой, которая наносит 10x урона Исаака#{{Warning}} Пока в руках, получение урона:#↓ Отнимает дополнительно 2 сердца здоровья#↓ Ломает пушку на несколько комнат#↑ {{Range}} +1.5 к дальности и оставляет кровавый след на комнату#Дополнительный урон не может убить Исаака#Самоповреждение не активирует эффект"}, -- Glass Cannon
	[354] = {"354", "Крэк Джекс", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Лечит 1 сердце#{{Trinket}} Создаёт аксессуар"}, -- Crack Jacks
	[355] = {"355", "Мамины жемчужины", "↑ {{Range}} +2.5 к дальности#↑ {{Luck}} +1 к удаче#{{SoulHeart}} +1 сердце души"}, -- Mom's Pearls
	[360] = {"360", "Инкуб", "Стреляет слезами с теми же эффектами, что и у Исаака#{{Damage}} Наносит 75% урона Исаака"}, -- Incubus
	[365] = {"365", "Потерянная муха", "Движется вдоль стен/препятствий в комнате#Наносит 30 контактного урона в секунду#Ближайшие враги целятся в муху"}, -- Lost Fly
	[366] = {"366", "Кассетные бомбы", "{{Bomb}} +5 бомб#Бомбы Исаака разлетаются на 4-5 крошечных бомб"}, -- Scatter Bombs
	[367] = {"367", "Липкие бомбы", "{{Bomb}} +5 бомб#{{Slow}} Бомбы Исаака прилипают к врагам и оставляют белую замедляющую жижу#Убийство врага бомбой создаёт синих пауков"}, -- Sticky Bombs
	[368] =	{"368", "Эпифора", "↑ {{Tears}} Стрельба в одном направлении постепенно увеличивает скорострельность до 200% и снижает точность"}, -- Epiphora
	[369] = {"369", "Континуум", "↑ {{Range}} +3 к дальности#Призрачные слёзы#Слёзы могут проходить через одну сторону экрана и выходить с другой"}, -- Continuum
	[370] = {"370", "Мистер Долли", "↑ {{Tears}} +0.7 к скорострельности#↑ {{Range}} +2.5 к дальности#{{UnknownHeart}} Создаёт 3 случайных сердца"}, -- Mr. Dolly
	[374] = {"374", "Святой свет", "10% шанс выстрелить святыми слезами, которые вызывают луч света при попадании#{{Luck}} 50% шанс при 9 удачи#{{Damage}} Лучи наносят 3x урона Исаака"}, -- Holy Light
	[375] = {"375", "Шапка-хост", "Даёт иммунитет к взрывам и падающим снарядам#25% шанс отразить вражеские выстрелы"}, -- Host Hat
	[376] = {"376", "Пополнение запасов", "Покупка предмета в магазине мгновенно пополняет его#Цена пополненных предметов каждый раз увеличивается"}, -- Restock
	[380] = {"380", "Плати, чтобы играть", "{{Coin}} +5 монет#Запертые блоки, двери и сундуки должны открываться монетами вместо ключей"}, -- Pay To Play
	[382] = {"382", "Дружелюбный шар", "Можно бросить во врагов, чтобы захватить их#{{Friendly}} Использование предмета после захвата врага создаёт его как дружелюбного спутника#Прохождение по шару после захвата мгновенно перезаряжает предмет"}, -- Friendly Ball
	[384] = {"384", "Маленький Гёрди", "{{Chargeable}} Запускается и отскакивает по комнате со скоростью, зависящей от уровня заряда#Наносит 5-20 контактного урона за удар в зависимости от скорости"}, -- Lil Gurdy
	[386] = {"386", "D12", "Перебрасывает любое препятствие в другое случайное препятствие (например, какашки, горшки, ТНТ, красные какашки, каменные блоки и т.д.)#Небольшой шанс перебросить препятствия в кнопки, выключатели, потайные комнаты или люки"}, -- D12
	[389] = {"389", "Сумка с рунами", "{{Rune}} Создаёт случайную руну или камень души каждые 7-8 комнат"}, -- Rune Bag
	[391] = {"391", "Предательство", "Враги могут поражать друг друга своими снарядами и начинают драться между собой"}, -- Betrayal
	[393] = {"393", "Поцелуй змеи", "{{Poison}} 15% шанс выстрелить ядовитыми слезами#{{Poison}} Отравляет врагов при контакте#{{BlackHeart}} Враги, убитые контактным ядом, имеют шанс выпадения чёрного сердца после смерти"}, -- Serpent's Kiss
	[394] = {"394", "Отмеченный", "↑ {{Tears}} +0.7 к скорострельности#↑ {{Range}} +3 к дальности#Исаак автоматически стреляет слезами по подвижной красной цели на земле#Фамильяры тоже стреляют в сторону цели#Вы можете прекратить стрельбу и сбросить местоположение цели, нажав кнопку сброса ({{ButtonRT}})"}, -- Marked
	[395] = {"395", "Технология X", "Слёзы Исаака заменяются заряжаемым лазерным кольцом#Размер и урон кольца увеличиваются до 100% со временем заряда"}, -- Tech X
	[397] = {"397", "Тянущий луч", "↑ {{Tears}} +1 к скорострельности#↑ {{Range}} +2.5 к дальности#↑ {{Shotspeed}} +0.16 к скорости полёта слёз#Слёзы Исаака всегда движутся вдоль луча света перед ним"}, -- Tractor Beam
	[399] = {"399", "Пасть Пустоты", "{{Chargeable}} Стрельба слезами в течение 2.35 секунд и отпускание кнопки стрельбы создаёт чёрное кольцо бримстоуна вокруг Исаака#Оно наносит 30x урона Исаака за 2 секунды"}, -- Maw of the Void
	[401] = {"401", "Эксплозиво", "25% шанс выстрелить липкими слезами#Липкие слёзы растут и взрываются через несколько секунд, нанося урон Исаака +60"}, -- Explosivo
	[404] = {"404", "Пердящий ребёнок", "Блокирует снаряды#При попадании 10% шанс перднуть и {{Charm}} очаровать, {{Poison}} отравить или отбросить врагов#Пердёж наносит 5-6 урона"}, -- Farting Baby
	[405] = {"405", "GB-жук", "{{Throwable}} Метательный (двойное нажатие на стрельбу)#Перебрасывает врагов и подбираемые предметы, с которыми вступает в контакт"}, -- GB Bug
	[407] = {"407", "Чистота", "↑ Усиливает одну из характеристик Исаака в зависимости от цвета ауры#Получение урона убирает эффект и даёт новый эффект в следующей комнате#{{ColorYellow}}Жёлтый{{CR}} = ↑ {{Speed}} +0.5 к скорости#{{ColorBlue}}Синий{{CR}} = ↑ {{Tears}} +2 к скорострельности#{{ColorRed}}Красный{{CR}} = ↑ {{Damage}} +4 к урону#{{ColorOrange}}Оранжевый{{CR}} = ↑ {{Range}} +3 к дальности"}, -- Purity
	[408] = {"408", "Атаме", "25% шанс появления чёрного кольца бримстоуна вокруг убитых врагов#Оно наносит 30x урона Исаака за 2 секунды#{{Luck}} +2.5% шанс за удачу"}, -- Athame
	[415] = {"415", "Корона света", "{{SoulHeart}} +2 сердца души#Если у Исаака нет повреждённых контейнеров сердца:#↑ {{Damage}} x2 множитель урона#↓ {{Shotspeed}} -0.3 к скорости полёта слёз#Получение любого урона убирает эффект на комнату"}, -- Crown Of Light
	[416] = {"416", "Глубокие карманы", "{{Coin}} Если зачистка комнаты не даёт награды, создаёт 1-3 монеты#{{Coin}} Увеличивает лимит монет до 999"}, -- Deep Pockets
	[417] = {"417", "Суккуб", "Отскакивает по комнате, окружённая аурой, наносящей 7.5-10 урона в секунду#↑ {{Damage}} x1.5 множитель урона, стоя в ауре"}, -- Succubus
	[419] = {"419", "Телепорт 2.0", "Телепортирует Исаака в комнату, которая ещё не была зачищена#Иерархия: {{Room}}>{{BossRoom}}>{{SuperSecretRoom}}>{{Shop}}>{{TreasureRoom}}>{{SacrificeRoom}}> {{DiceRoom}}>{{Library}}>{{CursedRoom}}>{{MiniBoss}}>{{ChallengeRoom}}{{BossRushRoom}}>{{IsaacsRoom}}{{BarrenRoom}}> {{ArcadeRoom}}>{{ChestRoom}}>{{Planetarium}}>{{SecretRoom}}>{{DevilRoom}}{{AngelRoom}}>{{ErrorRoom}}"}, -- Teleport 2.0
	[421] = {"421", "Почечная фасоль", "{{Charm}} Очаровывает всех врагов в ближнем радиусе"}, -- Kidney Bean
	[422] = {"422", "Светящиеся песочные часы", "Возвращает Исаака в предыдущую комнату и отменяет все действия, совершённые в комнате, где был использован предмет#Перемотку можно использовать три раза за этаж#{{Collectible66}} Действует как Песочные часы, когда перемотки закончились, что замедляет врагов на 8 секунд"}, -- Glowing Hourglass
	[426] = {"426", "Навязчивый фанат", "Повторяет движения Исаака с задержкой 0.66 секунды#Наносит 30 контактного урона в секунду"}, -- Obsessed Fan
	[430] = {"430", "Папа-муха", "Повторяет движения Исаака с задержкой 0.66 секунды#{{Damage}} Стреляет слезами по ближайшим врагам, которые наносят урон Исаака"}, -- Papa Fly
	[431] = {"431", "Многомерный ребёнок", "Повторяет движения Исаака с задержкой 0.66 секунды#Слёзы, проходящие через него, удваиваются и получают бонус к дальности и скорости полёта"}, -- Multidimensional Baby
	[432] = {"432", "Блестящие бомбы", "{{Bomb}} +5 бомб#{{Charm}} Бомбы имеют 63% шанс выпадения случайного подбираемого предмета и 15% шанс очаровать врагов при взрыве#Шанс выпадения подбираемого предмета уменьшается на 1% за каждое выпадение на этом этаже"}, -- Glitter Bombs
	[433] = {"433", "Моя тень", "Маленькая тень следует за Исааком#{{Friendly}} Когда враг касается тени, на комнату появляется дружелюбный чёрный налётчик#Налётчик наносит 8.7 урона за удар"}, -- My Shadow
	[437] = {"437", "D7", "Перезапускает комнату и возрождает всех врагов#Может использоваться для получения нескольких наград за зачистку одной комнаты"}, -- D7
	[440] = {"440", "Почечный камень", "Исаак время от времени прекращает стрельбу и заряжает атаку, которая выпускает очередь слёз и почечный камень"}, -- Kidney Stone
	[442] = {"442", "Корона тёмного принца", "При 1 полном красном сердце:#↑ {{Tears}} +2 к скорострельности#↑ {{Range}} +1.5 к дальности#↑ {{Shotspeed}} +0.2 к скорости полёта слёз"}, -- Dark Prince's Crown
	[444] = {"444", "Свинцовый карандаш", "Исаак выстреливает очередью слёз каждые 15 слёз"}, -- Lead Pencil
	[448] = {"448", "Осколок стекла", "При получении урона:#{{Heart}} 25% шанс создать красное сердце#{{BleedingOut}} Исаак истекает кровью, извергая слёзы в направлении, в котором стреляет#Кровотечение наносит половину красного сердца урона каждые 20 секунд#Кровотечение прекращается, если исцеляется красное сердце, все красные сердца пусты, или следующий урон убьёт Исаака"}, -- Shard of Glass
	[450] = {"450", "Глаз жадности", "{{Damage}} Каждые 20 слёз Исаак выстреливает слезой-монетой, которая наносит x1.5 +10 урона#{{Petrify}} Враги, поражённые монетой, окаменевают и превращаются в золото#{{Coin}} Убийство золотого врага даёт 1-3 монеты#{{Warning}} Выстрел слезой-монетой стоит 1 монету"}, -- Eye of Greed
	[451] = {"451", "Ткань для таро", "{{Card}} Создаёт карту#{{Card}} Эффекты карт Таро удваиваются или усиливаются"}, -- Tarot Cloth
	[453] = {"453", "Сложный перелом", "↑ {{Range}} +1.5 к дальности#Слёзы разбиваются на 1-3 костяных осколка при попадании во что-либо"}, -- Compound Fracture
	[455] = {"455", "Папина потерянная монета", "↑ {{Range}} +2.5 к дальности#{{Luck}} Создаёт счастливый пенни"}, -- Dad's Lost Coin
	[456] = {"456", "Полночный перекус", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Лечит 1 сердце"}, -- Midnight Snack
	[459] = {"459", "Синусовая инфекция", "20% шанс выстрелить липкой соплёй#{{Damage}} Сопли наносят урон Исаака раз в секунду и прилипают на 10 секунд#{{Luck}} Не зависит от удачи"}, -- Sinus Infection
	[462] = {"462", "Глаз Белиала", "↑ {{Range}} +1.5 к дальности#Пронзающие слёзы#Попадание во врага делает слезу самонаводящейся и удваивает её урон"}, -- Eye of Belial
	[464] = {"464", "Глиф баланса", "{{SoulHeart}} +2 сердца души#Награды за зачистку комнат и выпадения с чемпионов становятся тем подбираемым предметом, в котором Исаак нуждается больше всего"}, -- Glyph of Balance
	[468] = {"468", "Тень", "Следует за движениями Исаака с задержкой 0.66 секунды#Наносит 75 контактного урона в секунду#{{Friendly}} После нанесения 666 урона поглощается Исааком, увеличивая его контактный урон и создавая дружелюбных налётчиков"}, -- Shade
	[472] = {"472", "Королевский ребёнок", "Другие фамильяры следуют за ним и автоматически стреляют по врагам#Прекращает движение, когда Исаак стреляет#Телепортируется обратно к Исааку, когда он прекращает стрелять"}, -- King Baby
	[474] = {"474", "Сломанная стеклянная пушка", "Использование предмета превращает его обратно в Стеклянную пушку"}, -- Broken Glass Cannon
	[476] =	{"476", "D1", "Дублирует случайный подбираемый предмет в комнате#Дублированные подбираемые предметы могут быть не идентичны оригиналу"}, -- D1
	[477] = {"477", "Пустота", "Поглощает все предметы на пьедесталах в комнате#Активные предметы: их эффекты активируются при каждом последующем использовании Пустоты#↑ Пассивные предметы дают два случайных повышения характеристик"}, -- Void
	[487] = {"487", "Картофелечистка", "{{EmptyHeart}} Убирает 1 контейнер сердца за:#↑ {{Damage}} +0.2 к урону#{{Collectible73}} Куб мяса#{{Timer}} Даёт на комнату:#↑ {{Range}} +1.5 к дальности#{{Collectible214}} Оставляет след кровавой жижи"}, -- Potato Peeler
	[489] = {"489", "D Бесконечность", "Может действовать как любой предмет-кубик (кроме {{Collectible723}} Спин-даун кубиков) с помощью кнопки сброса ({{ButtonRT}})#Время заряда зависит от последнего использованного кубика и обновляется при каждом использовании"}, -- D Infinity
	[491] = {"491", "Кислотный ребёнок", "{{Pill}} Создаёт случайную пилюлю каждые 7 комнат#{{Poison}} Использование пилюли отравляет всех врагов в комнате"}, -- Acid Baby
	[493] = {"493", "Адреналин", "↑ {{Damage}} Увеличение урона за каждый пустой контейнер сердца#Чем больше пустых контейнеров сердца, тем больше бонус за каждый новый"}, -- Adrenaline
	[494] = {"494", "Лестница Иакова", "Слёзы создают искру электричества при ударе#Искры наносят половину урона Исаака#Искры могут перескакивать до 4 других врагов"}, -- Jacob's Ladder
	[495] = {"495", "Призрачный перец", "8% шанс выстрелить синим огнём, который блокирует вражеские выстрелы и наносит контактный урон#{{Luck}} 50% шанс при 10 удачи#Огни сжимаются и исчезают через 2 секунды"}, -- Ghost Pepper
	[496] = {"496", "Эвтаназия", "3.33% шанс выстрелить иглой#{{Luck}} 25% шанс при 13 удачи#Иглы мгновенно убивают обычных врагов, разрывая их на 10 слёз#{{Damage}} Иглы наносят 3x урона Исаака боссам"}, -- Euthanasia
	[497] = {"497", "Камуфляжные трусы", "{{Confusion}} Вход в комнату маскирует Исаака и сбивает с толку всех врагов, пока не будет выпущена слеза#↑ {{Speed}} +0.5 к скорости в маскировке#Снятие маскировки наносит урон вокруг Исаака и даёт очень кратковременное повышение скорострельности и урона"}, -- Camo Undies
	[500] = {"500", "Мешок с мешками", "Создаёт мешок каждые 7-8 комнат"}, -- Sack of Sacks
	[501] = {"501", "Глотка Жадности", "{{Heart}} +1 контейнер сердца за каждые 25 монет у Исаака"}, -- Greed's Gullet
	[503] = {"503", "Маленький Рог", "5% шанс выстрелить слезой, которая вызывает руку Большого Рога#{{Luck}} 20% шанс при 15 удачи#Рука мгновенно убивает врагов и наносит 36 урона боссам#Исаак наносит 7 контактного урона в секунду"}, -- Little Horn
	[504] = {"504", "Коричневый самородок", "Создаёт муху, которая стреляет по врагам#Каждый выстрел наносит 3.5 урона"}, -- Brown Nugget
	[506] = {"506", "Удар в спину", "{{BleedingOut}} Попадание во врага в спину наносит двойной урон и вызывает кровотечение, из-за которого враги оставляют жижу и получают урон при движении"}, -- Backstabber
	[507] = {"507", "Острая соломинка", "{{Damage}} Наносит урон Исаака + 10% от максимального здоровья врага всем врагам#{{HalfHeart}} Нанесение урона Соломинкой может создавать половины сердец"}, -- Sharp Straw
	[508] = {"508", "Мамина бритва", "{{BleedingOut}} Орбитальный спутник, вызывающий кровотечение, из-за которого враги получают урон при движении#{{Damage}} Наносит 1.5x урона Исаака в секунду"}, -- Mom's Razor
	[509] = {"509", "Кровоточащий глаз", "Орбитальный спутник, который выстреливает слезой каждые 0.33 секунды по ближайшим врагам#Наносит 3.5 урона за слезу#Наносит 20 контактного урона в секунду"}, -- Bloodshot Eye
	[514] = {"514", "Сломанный модем", "Заставляет некоторых врагов и снаряды на короткое время замирать с случайными интервалами#Замершие снаряды исчезают#25% шанс удвоить награды за зачистку комнаты"}, -- Broken Modem
	[517] = {"517", "Быстрые бомбы", "{{Bomb}} +7 бомб#Убирает задержку между установкой бомб#Бомбы не отталкивают друг друга"}, -- Fast Bombs
	[522] = {"522", "Телекинез", "Останавливает все вражеские снаряды, которые приближаются к Исааку, на 3 секунды и затем отбрасывает их от него#Отталкивает близких врагов во время действия эффекта"}, -- Telekinesis
	[523] = {"523", "Передвижной ящик", "Хранит до 10 подбираемых предметов и предметов из текущей комнаты#Повторное использование предмета возвращает всё на пол#Позволяет Исааку перемещать вещи между комнатами"}, -- Moving Box
	[524] = {"524", "Технология Ноль", "Слёзы Исаака соединены электрическими лучами#Электричество наносит 4.5x урона Исаака в секунду"}, -- Technology Zero
	[531] = {"531", "Гемолакрия", "↑ {{Damage}} +1 к урону#↑ {{Damage}} x1.5 множитель урона#↓ {{Tears}} x0.33 множитель скорострельности#↓ {{Range}} x0.8 множитель дальности#Слёзы Исаака летят по дуге и разрываются на более мелкие слёзы при ударе"}, -- Haemolacria
	[543] = {"543", "Освящённая земля", "Получение урона создаёт белую какашку#Находясь в ауре какашки:#↑ {{Tears}} x2.5 множитель скорострельности#↑ {{Damage}} x1.2 множитель урона#Самонаводящиеся слёзы#Шанс блокировать урон"}, -- Hallowed Ground
	[549] =	{"549", "Хрупкие кости", "{{EmptyBoneHeart}} Заменяет все контейнеры красных сердец Исаака на 6 пустых костяных сердец#При потере костяного сердца:#↑ {{Tears}} +0.4 к скорострельности#Выстреливает 8 костяными слезами во всех направлениях"}, -- Brittle Bones
	[552] = {"552", "Мамина лопата", "Создаёт люк на следующий этаж#{{LadderRoom}} Создаёт потайную комнату, если использовать на декоративной плитке пола (трава, маленькие камни, бумаги, драгоценности и т.д.)#{{Warning}} Используйте лопату на куче земли в \"Тёмной комнате\""}, -- Mom's Shovel
	[553] = {"553", "Мукормикоз", "25% шанс выстрелить липкой споровой слезой#{{Luck}} Не зависит от удачи#{{Poison}} Споры взрываются через 2.5 секунды, нанося урон, отравляя ближайших врагов и выпуская больше спор"}, -- Mucormycosis
	[554] = {"554", "2Жутко", "{{Fear}} Пугает врагов в небольшом радиусе вокруг Исаака"}, -- 2Spooky
	[555] = {"555", "Золотая бритва", "{{Coin}} +5 монет при подборе#{{Timer}} Заплатите 5 {{Coin}} монет и получите на комнату:#↑ {{Damage}} +1.2 к урону"}, -- Golden Razor
	[556] = {"556", "Сера", "{{Timer}} {{Collectible118}} Бримстоун на комнату#Использование несколько раз в одной комнате даёт увеличенный урон и больший луч"}, -- Sulfur
	[557] = {"557", "Печенье с предсказанием", "Даёт одну из следующих наград:#Предсказание#{{SoulHeart}} Сердце души#{{Rune}} Руну или камень души#{{Card}} Карту Таро#{{Trinket}} Аксессуар"}, -- Fortune Cookie
	[558] = {"558", "Боль в глазу", "Шанс выстрелить 1-3 дополнительными слезами в случайных направлениях#{{Luck}} Не зависит от удачи"}, -- Eye Sore
	[559] = {"559", "120 Вольт", "Неоднократно бьёт током ближайших врагов#{{Damage}} Электричество наносит до 3.75x урона Исаака в секунду#Искры могут перескакивать до 4 других врагов"}, -- 120 Volt
	[560] = {"560", "Больно", "{{Timer}} При получении урона даёт на комнату:#↑ {{Tears}} +1.2 к скорострельности при первом ударе#↑ {{Tears}} +0.4 к скорострельности за каждый дополнительный удар#Выпускает кольцо из 10 слёз вокруг Исаака"}, -- It Hurts
	[561] = {"561", "Миндальное молоко", "↑ {{Tears}} x4 множитель скорострельности#↓ {{Damage}} x0.3 множитель урона#↓ {{Tearsize}} -0.16 размер слезы#Слёзы получают случайные эффекты аксессуаров-червей и некоторые эффекты предметов"}, -- Almond Milk
	[562] = {"562", "Каменное дно", "↑ Предотвращает понижение характеристик до конца забега"}, -- Rock Bottom
	[563] = {"563", "Бомбы Нэнси", "{{Bomb}} +5 бомб#Бомбы Исаака взрываются со случайными эффектами"}, -- Nancy Bombs
	[564] = {"564", "Кусок мыла", "↑ {{Tears}} +0.5 к скорострельности#↑ {{Shotspeed}} +0.2 к скорости полёта слёз"}, -- A Bar of Soap
	[565] = {"565", "Кровавый щенок", "Преследует врагов#После убийства 15 врагов наносит больше урона, создаёт половину красного сердца каждые 10 убийств, но пытается навредить Исааку#После убийства 40 врагов наносит ещё больше урона, создаёт полные красные сердца и может разрушать камни#Нанесение достаточного урона возвращает его в первую фазу"}, -- Blood Puppy
	[566] = {"566", "Ловец снов", "{{HalfSoulHeart}} +1 половина сердца души при входе на новый этаж#Кошмар при переходе между этажами показывает босса следующего этажа и предмет из комнаты сокровищ"}, -- Dream Catcher
	[567] = {"567", "Пасхальная свеча", "↑ {{Tears}} Зачистка комнаты без получения урона даёт +0.4 к скорострельности#Максимум +2 к скорострельности {{ColorSilver}}(5 комнат){{CR}}"}, -- Paschal Candle
	[568] = {"568", "Божественное вмешательство", "Двойное нажатие на кнопку стрельбы создаёт щит#Щит длится 1 секунду, отталкивает врагов и отражает вражеские снаряды и лазеры"}, -- Divine Intervention
	[569] = {"569", "Кровавая клятва", "{{Warning}} Вход на новый этаж истощает все красные сердца Исаака, но даёт бонусы к скорости и урону за каждое потерянное сердце#Каждая потерянная половина сердца считается отдельным ударом для эффектов при попадании"}, -- Blood Oath
	[570] = {"570", "Печенье из пластилина", "Каждая слеза Исаака имеет разный цвет и эффект статуса"}, -- Playdough Cookie
	[571] = {"571", "Сиротские носки", "↑ {{Speed}} +0.3 к скорости#{{SoulHeart}} +2 сердца души#Иммунитет к жиже и напольным шипам"}, -- Orphan Socks
	[572] = {"572", "Глаз оккультиста", "↑ {{Damage}} +1 к урону#↑ {{Range}} +2 к дальности#↓ {{Shotspeed}} -0.16 к скорости полёта слёз#Слёзы Исаака можно контролировать в полёте"}, -- Eye of the Occult
	[573] = {"573", "Непорочное сердце", "↑ {{Heart}} +1 к здоровью#↑ {{Damage}} x1.2 множитель урона#{{HealingRed}} Полное здоровье#20% шанс выстрелить дополнительной орбитальной призрачной слезой"}, -- Immaculate Heart
	[574] = {"574", "Дароносица", "Исаак окружён аурой, наносящей урон#Чем ближе враги к Исааку, тем больше урона наносит им аура"}, -- Monstrance
	[575] = {"575", "Незваный гость", "{{Slow}} Зарывается в голову Исаака и выстреливает 4 дополнительными замедляющими слезами, которые наносят 1.5 урона#Получение урона может заставить паука выйти из головы и преследовать врагов"}, -- The Intruder
	[576] = {"576", "Грязные мысли", "Все враги-какашки (маленькие какашки) дружелюбны#Разрушение какашек создаёт 1-4 какашки#Тип какашки зависит от типа какашки#Камни могут быть заменены какашками"}, -- Dirty Mind
	[577] = {"577", "Дамоклов меч", "Над головой Исаака висит меч, который удваивает все предметы на пьедесталах#Не удваивает предметы, у которых есть цена или которые находятся в сундуках#{{Warning}} После получения любого урона меч имеет чрезвычайно низкий шанс мгновенно убить Исаака каждый кадр#Эффекты неуязвимости могут предотвратить смерть"}, -- Damocles
	[578] = {"578", "Бесплатный лимонад", "Создаёт большую лужу жёлтой жижи#Жижа наносит 24 урона в секунду"}, -- Free Lemonade
	[579] = {"579", "Духовный меч", "Вместо стрельбы слезами, размахивает мечом#{{Damage}} Меч наносит 3x урона Исаака +3.5 и размахивает так же быстро, как нажимается кнопка стрельбы#{{Chargeable}} Зарядка делает вращательную атаку + выстрел снарядом#Стреляет снарядами при взмахах на полном здоровье"}, -- Spirit Sword
	[580] = {"580", "Красный ключ", "Создаёт красную комнату рядом с обычной комнатой, обозначенную контуром двери#Красные комнаты могут быть специальными комнатами#{{ErrorRoom}} Вход в комнату за пределами карты этажа 13x13 телепортирует Исаака в комнату Я ОШИБКА"}, -- Red Key
	[581] = {"581", "Пси-муха", "Преследует и отражает вражеские снаряды#Наносит 15 контактного урона в секунду"}, -- Psy Fly
	[582] = {"582", "Волнистая шапка", "↑ {{Tears}} +0.75 к скорострельности#↓ {{Speed}} -0.03 к скорости#Искажает экран#Требуется больше времени для перезарядки при каждом использовании#Выход или зачистка комнат уменьшает эффекты"}, -- Wavy Cap
	[583] = {"583", "Ракета в банке", "{{Bomb}} +5 бомб#Установка бомбы во время стрельбы запускает ракету в этом направлении"}, -- Rocket in a Jar
	[584] = {"584", "Книга добродетелей", "{{AngelChance}} +12.5% шанс на комнату Ангела, пока предмет в руках#Создаёт орбитального фамильяра-огонька, который стреляет призрачными слезами, но может быть уничтожен#Может комбинироваться со вторым активным предметом для создания особых огоньков#{{AngelRoom}} Превращает первую комнату Дьявола в комнату Ангела#{{AngelRoom}} Позволяет комнатам Ангела появляться после сделки с Дьяволом"}, -- Book of Virtues
	[585] = {"585", "Алебастровая шкатулка", "Должна заряжаться подбором сердец души, затем создаёт:#{{SoulHeart}} Три сердца души#{{AngelRoom}} Два предмета из комнаты Ангела#{{DevilRoom}} Создаёт только 2 сердца души и 1 предмет Ангела, если была совершена сделка с Дьяволом"}, -- Alabaster Box
	[586] = {"586", "Лестница", "Создаёт лестницу в первой комнате каждого этажа, которая ведёт в уникальный {{AngelRoom}} магазин комнаты Ангела с предметами и подбираемыми предметами"}, -- The Stairway
	[587] = {"587", "", "<Предмет не существует>"},
	[588] = {"588", "Солнце", "{{BossRoom}} Показывает местоположение комнаты Босса#{{Timer}} Когда босс этажа побеждён, даёт на этаж:#↑ {{Damage}} +3 к урону#↑ {{Luck}} +1 к удаче#{{Card20}} Эффект Солнца#{{Battery}} Полностью перезаряжает активный предмет#{{CurseBlind}} Снимает любые проклятия"}, -- Sol
	[589] = {"589", "Луна", "Добавляет дополнительную {{SecretRoom}} Секретную комнату и {{SuperSecretRoom}} Суперсекретную комнату на каждый этаж#Показывает одну Секретную комнату на каждом этаже#{{Timer}} Секретные комнаты содержат луч света, который даёт на этаж:#↑ {{Tears}} +0.5 к скорострельности#↑ {{Tears}} Дополнительно +0.5 к скорострельности от первого луча за этаж#{{HalfSoulHeart}} Половина сердца души"}, -- Luna
	[590] = {"590", "Меркурий", "↑ {{Speed}} +0.4 к скорости#Большинство дверей остаются постоянно открытыми"}, -- Mercurius
	[591] = {"591", "Венера", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Лечит 1 сердце#{{Charm}} Очаровывает ближайших врагов"}, -- Venus
	[592] = {"592", "Терра", "↑ {{Damage}} +1 к урону#Заменяет слёзы Исаака на камни#Камни наносят переменный урон, могут разрушать препятствия и имеют увеличенное отбрасывание"}, -- Terra
	[593] = {"593", "Марс", "Двойное нажатие на клавишу движения заставляет Исаака делать рывок#{{Damage}} Во время рывка Исаак неуязвим и наносит 4x своего урона +8#{{Timer}} 3 секунды перезарядки"}, -- Mars
	[594] = {"594", "Юпитер", "↑ {{EmptyHeart}} +2 пустых контейнера сердца#↓ {{Speed}} -0.3 к скорости#{{HealingRed}} Лечит половину сердца#{{Speed}} Скорость увеличивается до +0.5, когда стоит на месте#{{Poison}} Движение выпускает облака яда#{{Poison}} Иммунитет к яду"}, -- Jupiter
	[595] = {"595", "Сатурн", "Вход в комнату заставляет 7 слёз вращаться вокруг Исаака#Эти слёзы длятся 13 секунд и наносят 1.5x урона Исаака +5#Вражеские снаряды имеют шанс вращаться вокруг Исаака"}, -- Saturnus
	[596] = {"596", "Уран", "{{Freezing}} Исаак стреляет окаменяющими слезами, которые замораживают врагов после смерти#Касание замороженного врага заставляет его скользить и взрываться на 10 ледяных осколков"}, -- Uranus
	[597] = {"597", "Нептун", "{{Tears}} Не стреляя, накапливается бонус к слезам за 3 секунды#Бонус к слезам уменьшается, когда Исаак стреляет"}, -- Neptunus
	[598] = {"598", "Плутон", "↑ {{Tears}} +0.7 к скорострельности#Значительно уменьшает Исаака, позволяя ему протискиваться между объектами#Снаряды могут пролетать над ним"}, -- Pluto
	[599] = {"599", "Голова вуду", "{{CursedRoom}} Создаёт дополнительную комнату Проклятий на каждом этаже#Улучшает планировку и награды комнат Проклятий#{{Coin}} Создаёт монету в каждой комнате Проклятий"}, -- Voodoo Head
	[600] = {"600", "Глазные капли", "↑ {{Tears}} x1.4 множитель скорострельности для левого глаза"}, -- Eye Drops
	[601] = {"601", "Акт покаяния", "↑ {{Tears}} +0.7 к скорострельности#{{EternalHeart}} +1 вечное сердце#{{AngelChance}} Позволяет комнатам Ангела появляться даже после сделки с дьяволом#Получение урона по красным сердцам не так сильно снижает шанс на комнату Дьявола/Ангела"}, -- Act of Contrition
	[602] = {"602", "Членская карта", "{{Shop}} Открывает люк в каждом магазине#Люк ведёт в подземный магазин, где продаются аксессуары, руны, карты, особые сердца и предметы из любого пула"}, -- Member Card
	[603] = {"603", "Батарейный блок", "{{Battery}} Создаёт 2-4 батарейки#{{Battery}} Полностью перезаряжает активный предмет"}, -- Battery Pack
	[604] = {"604", "Мамин браслет", "Позволяет Исааку подбирать и бросать камни, ТНТ, какашки, дружелюбных какашек, хостов и другие препятствия#Позволяет переносить их между комнатами"}, -- Mom's Bracelet
	[605] = {"605", "Совок", "↑ {{Damage}} x1.35 множитель урона для правого глаза#{{Timer}} Призывает фамильяра-подглядывающего на комнату, который оставляет след красной жижи и наносит 36 контактного урона в секунду"}, -- The Scooper
	[606] = {"606", "Окулярный разлом", "5% шанс выстрелить слезами, которые создают разломы там, где приземляются#{{Luck}} 20% шанс при 15 удачи#Разломы наносят 3x урона Исаака в секунду и притягивают ближайших врагов, подбираемые предметы и снаряды"}, -- Ocular Rift
	[607] = {"607", "Варёный ребёнок", "Стреляет хаотичными очередями слёз во всех направлениях#Наносит 3.5 или 5.25 урона за слезу"}, -- Boiled Baby
	[608] = {"608", "Морозильный ребёнок", "{{Petrify}} Стреляет окаменяющими слезами, которые наносят 3.5 урона#{{Freezing}} Замораживает врагов после их убийства"}, -- Freezer Baby
	[609] = {"609", "Вечный D6", "Перебрасывает все предметы на пьедесталах в комнате#Имеет 25% шанс удалить предметы вместо их переброски"}, -- Eternal D6
	[610] = {"610", "Птичья клетка", "Прыгает на врага, который наносит первый урон Исааку в комнате#Наносит 45 урона и выпускает каменную волну#После этого преследует врагов, нанося 6.5 контактного урона в секунду"}, -- Bird Cage
	[611] = {"611", "Гортань", "Исаак кричит, нанося урон и отбрасывая ближайших врагов#Крик становится сильнее, чем больше зарядов у предмета"}, -- Larynx
	[612] = {"612", "Потерянная душа", "Умирает от одного удара и возрождается в начале следующего этажа#Если доживает до следующего этажа, может создать:#{{SoulHeart}} 3 сердца души#{{EternalHeart}} 2 вечных сердца#{{TreasureRoom}} Предмет из комнаты сокровищ#{{AngelRoom}} Предмет из комнаты Ангела"}, -- Lost Soul
	[613] = {"613", "", "<Предмет не существует>"},
	[614] = {"614", "Кровавые бомбы", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Лечит 4 сердца#{{HalfHeart}} Если у Исаака нет бомб, можно поставить одну ценой половины сердца#Бомбы Исаака оставляют красную жижу#"}, -- Blood Bombs
	[615] = {"615", "Маленький Дампи", "Отклоняет врага или снаряд прямо перед тем, как Исаак получил бы от него урон#Шанс отклонить снаряды рядом с ним#Нужно коснуться после отклонения, чтобы снова активировать"}, -- Lil Dumpy
	[616] = {"616", "Птичий глаз", "8% шанс выстрелить красным огнём, который блокирует вражеские выстрелы и наносит контактный урон#{{Luck}} 50% шанс при 10 удачи#Огни исчезают после блокировки 4 выстрелов, нанесения урона 4 раза или через 10 секунд"}, -- Bird's Eye
	[617] = {"617", "Магнетит", "{{Magnetize}} 17% шанс выстрелить намагничивающими слезами#{{Luck}} 100% шанс при 5 удачи#Намагниченные враги притягивают ближайшие подбираемые предметы, снаряды и врагов"}, -- Lodestone
	[618] = {"618", "Гнилой помидор", "{{Bait}} 17% шанс выстрелить слезами, которые помечают врагов#{{Luck}} 100% шанс при 5 удачи#Помеченные враги становятся целью для других врагов"}, -- Rotten Tomato
	[619] = {"619", "Право по рождению", "Имеет разный эффект для каждого персонажа"}, -- Birthright
	[620] = {"620", "", "<Предмет не существует>"},
	[621] = {"621", "Красное рагу", "↑ {{Damage}} +21.6 к урону#{{HealingRed}} Полное здоровье#Увеличение урона спадает через 3 минуты#Убийство врагов, пока эффект активен, продлевает его"}, -- Red Stew
	[622] = {"622", "Бытие", "Убирает все предметы и подбираемые предметы Исаака#Телепортирует Исаака в спальню с подбираемыми предметами и сундуками#За каждый убранный предмет Исаак может выбрать один из 3 предметов из того же пула#Выход из спальни переносит Исаака на следующий этаж"}, -- Genesis
	[623] = {"623", "Острый ключ", "{{Key}} +5 ключей#Бросает один из ключей Исаака в направлении, в котором он стреляет#Брошенные ключи наносят урон, разрушают препятствия и открывают двери#Враги, убитые ключами, могут дать содержимое сундука, включая предметы"}, -- Sharp Key
	[624] = {"624", "Бустер-пак", "{{Card}} Создаёт 5 случайных карт"}, -- Booster Pack
	[625] = {"625", "Мега-гриб", "Гигантифицирует Исаака и даёт:#↑ {{Damage}} x4 множитель урона#↑ {{Range}} +2 к дальности#↓ {{Tears}} -1.9 к скорострельности#Неуязвимость#Способность сокрушать врагов и препятствия#{{Timer}} Длится 30 секунд и сохраняется между комнатами и этажами"}, -- Mega Mush
	[626] = {"626", "Часть ножа 1", "Превращается в метательный нож, который наносит 25 урона в сочетании с {{Collectible627}} Частью ножа 2#Нож может открыть дверь из плоти"}, -- Knife Piece 1
	[627] = {"627", "Часть ножа 2", "Превращается в метательный нож, который наносит 25 урона в сочетании с {{Collectible626}} Частью ножа 1#Нож может открыть дверь из плоти"}, -- Knife Piece 2
	[628] = {"628", "Свидетельство о смерти", "Телепортирует Исаака на этаж, который содержит все предметы в игре#Выбор предмета с этого этажа телепортирует Исаака обратно в комнату, из которой он пришёл"}, -- Death Certificate
	[629] = {"629", "Бот-муха", "Стреляет защищёнными слезами, чтобы уничтожать вражеские снаряды#Наносит 3 контактного урона в секунду"}, -- Bot Fly
	[630] = {"630", "", "<Предмет не существует>"},
	[631] = {"631", "Мясной тесак", "Разделяет всех врагов в комнате на 2 меньшие версии с 40% здоровья#Враги, которые естественно разделяются (как Зависть), получают достаточно урона, чтобы разделиться#Наносит 25 урона врагам, которых нельзя разделить"}, -- Meat Cleaver
	[632] = {"632", "Злой амулет", "↑ {{Luck}} +2 к удаче#Иммунитет к эффектам {{Burning}} горения, {{Confusion}} замешательства, {{Fear}} страха и {{Poison}} яда"}, -- Evil Charm
	[633] = {"633", "Догма", "↑ {{Speed}} +0.1 к скорости#↑ {{Damage}} +2 к урону#Полёт и одноразовый щит {{HolyMantleSmall}} Святой мантии#{{Heart}} Лечит Исаака красными и душами сердцами, если у него меньше 6 сердец"}, -- Dogma
	[634] = {"634", "Чистилище", "Красные трещины появляются на земле во враждебных комнатах#Прохождение по трещинам вызывает самонаводящихся взрывающихся призраков"}, -- Purgatory
	[635] = {"635", "Стежки", "Создаёт фамильяра, который движется в направлении, в котором стреляет Исаак#При использовании Исаак меняется местами с фамильяром и становится на короткое время неуязвимым#Телепортация на вещи может повредить или уничтожить их"}, -- Stitches
	[636] = {"636", "Ключ R", "Перезапускает весь забег#Все собранные предметы, аксессуары, характеристики и подбираемые предметы сохраняются#Таймер не сбрасывается"}, -- R Key
	[637] = {"637", "Нокаутирующие капли", "{{Confusion}} 10% шанс выстрелить кулаком, который вызывает замешательство и сильное отбрасывание#{{Luck}} 100% шанс при 9 удачи#Враги получают урон, когда их отбрасывает в стену/препятствие"}, -- Knockout Drops
	[638] = {"638", "Ластик", "Бросает ластик, который мгновенно убивает врага#Предотвращает появление стёртого врага до конца забега#Наносит 15 урона боссам#Можно использовать только один раз за этаж"}, -- Eraser
	[639] = {"639", "Гадостное сердце", "{{RottenHeart}} +1 гнилое сердце"}, -- Yuck Heart
	[640] = {"640", "Урна с душами", "Извергает поток пламени#Убийство врага добавляет заряд в урну"}, -- Urn of Souls
	[641] = {"641", "Акелдама", "Создаёт цепь слёз за Исааком во враждебных комнатах#Слёзы наносят 3.5 урона"}, -- Akeldama
	[642] = {"642", "Волшебная кожа", "Создаёт предмет из пула предметов текущей комнаты#{{BrokenHeart}} Превращает 1 контейнер сердца или 1 костяное сердце или 2 сердца души в разбитое сердце#{{Warning}} Заменяет будущие предметы, если Исаак не держит его {{ColorSilver}}(33% после 1 использования, 50% после 2, 100% после 3)#Меньший шанс, если Волшебная кожа находится на пьедестале на текущем этаже"}, -- Magic Skin
	[643] = {"643", "Откровение", "{{SoulHeart}} +2 сердца души#Полёт#{{Chargeable}} Заряжаемый святой луч с высоким уроном#Не заменяет слёзы Исаака"}, -- Revelation
	[644] = {"644", "Утешительный приз", "↑ Увеличивает самую низкую характеристику Исаака из скорости, скорострельности, урона и дальности#Создаёт либо 3 {{Coin}} монеты, 1 {{Bomb}} бомбу, либо 1 {{Key}} ключ в зависимости от того, чего у Исаака меньше всего"}, -- Consolation Prize
	[645] = {"645", "Крошечная тома", "Большой орбитальный спутник, который блокирует выстрелы#Наносит 3.5 контактного урона в секунду#Разделяется на меньшие версии себя после получения 3 ударов#Меньшие версии разлетаются на синих пауков#Возрождается через 5 секунд после полного исчезновения"}, -- Tinytoma
	[646] = {"646", "Бримстоуновые бомбы", "{{Bomb}} +5 бомб#{{Collectible118}} Бомбы Исаака выпускают 4-сторонний кровавый луч#Лучи не вредят Исааку"}, -- Brimstone Bombs
	[647] = {"647", "4.5 Вольт", "Зачистка комнат больше не заряжает активные предметы#Нанесение урона врагам медленно заполняет полосу заряда#Урон, необходимый за заряд, увеличивается на каждом этаже"}, -- 4.5 Volt
	[648] = {"648", "", "<Предмет не существует>"},
	[649] = {"649", "Фруктовая слива", "Движется по диагонали по комнате, стреляя слезами на своём пути, которые наносят 3 урона#Наносит 6 контактного урона в секунду"}, -- Fruity Plum
	[650] = {"650", "Сливовая флейта", "{{Timer}} Призывает дружелюбного Малыша Сливу в комнату на 10 секунд"}, -- Plum Flute
	[651] = {"651", "Вифлеемская звезда", "Медленно движется из первой комнаты этажа в {{BossRoom}} комнату Босса#Движется быстрее, если вы впереди, и медленнее, если вы позади#Стояние в её ауре даёт:#↑ {{Tears}} x2.5 множитель скорострельности#↑ {{Damage}} x1.8 множитель урона#Самонаводящиеся слёзы#50% шанс проигнорировать урон"}, -- Star of Bethlehem
	[652] = {"652", "Кубический ребёнок", "Можно пинать, врезаясь в него#{{Slow}} Замедляет и наносит до 17.5 контактного урона в зависимости от скорости#{{Freezing}} Замораживает врагов, которых убивает"}, -- Cube Baby
	[653] = {"653", "Vade Retro", "Удержание предмета заставляет не-призрачных врагов создавать маленьких красных призраков после смерти#Использование предмета заставляет призраков взрываться#Использование предмета также убивает любых призрачных врагов (включая боссов), у которых осталось менее 50% здоровья"}, -- Vade Retro
	[654] = {"654", "Ложный диплом", "{{BlackHeart}} +1 чёрное сердце#{{Pill}} Идентифицирует все пилюли#Превращает все хорошие пилюли в плохие#↑ {{Damage}} Съедение пилюли, понижающей характеристику, даёт +0.6 к урону#{{BlackHeart}} Съедение других плохих пилюль создаёт чёрное сердце"}, -- False PHD
	[655] = {"655", "Крутись, чтобы победить", "Пассивно даёт орбитальный спутник, который блокирует вражеские выстрелы и наносит 10.5 контактного урона в секунду#Использование предмета даёт:#↑ {{Speed}} +0.5 к скорости#Увеличивает скорость и урон орбитальных спутников"}, -- Spin to Win
	[656] = {"656", "Дамоклов меч", "Над головой Исаака висит меч, который удваивает все предметы на пьедесталах#Не удваивает предметы, у которых есть цена или которые находятся в сундуках#{{Warning}} После получения любого урона меч имеет чрезвычайно низкий шанс мгновенно убить Исаака каждый кадр#Эффекты неуязвимости могут предотвратить смерть"}, -- Damocles (hidden passive version)
	[657] = {"657", "Васкулит", "Враги взрываются на слёзы после смерти, которые наследуют эффекты слёз Исаака"}, -- Vasculitis
	[658] = {"658", "Гигантская клетка", "Получение урона создаёт Минисаака#Минисааки преследуют и стреляют по ближайшим врагам"}, -- Giant Cell
	[659] = {"659", "Тропикамид", "↑ {{Range}} +2.5 к дальности#↑ {{Tearsize}} +0.22 размер слезы"}, -- Tropicamide
	[660] = {"660", "Чтение карт", "Создаёт два портала в первой комнате каждого этажа#Выход из комнаты убирает порталы#{{Blank}} {{ColorRed}}Красный: {{CR}}{{BossRoom}} Комната босса#{{Blank}} {{ColorYellow}}Жёлтый: {{CR}}{{TreasureRoom}} Комната с предметом#{{Blank}} {{ColorBlue}}Синий: {{CR}}{{SecretRoom}} Секретная комната"}, -- Card Reading
	[661] = {"661", "Квинты", "Убийство врага создаёт стационарного фамильяра на его месте#Максимум 5 фамильяров"}, -- Quints
	[662] = {"662", "", "<Предмет не существует>"},
	[663] = {"663", "Зуб и ноготь", "1 секунда неуязвимости каждые 6 секунд#Исаак мигает прямо перед срабатыванием эффекта"}, -- Tooth and Nail
	[664] = {"664", "Обжора", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Полное здоровье#Пьедесталы с предметами циклически меняются между своим предметом и едой#Подбор еды даёт:#{{HealingRed}} Лечит 2 сердца#↑ {{Damage}} Временно +3.6 к урону#↑ 2 постоянных повышения характеристик (в зависимости от еды)#↓ {{Speed}} -0.03 к скорости"}, -- Binge Eater
	[665] = {"665", "Глаз Гуппи", "Показывает содержимое {{Chest}} сундуков, {{GrabBag}} мешков, лавочников и каминов до того, как они будут открыты или уничтожены"}, -- Guppy's Eye
	[666] = {"666", "", "<Предмет не существует>"},
	[667] = {"667", "Соломенное чучело", "{{Bomb}} +1 бомба#{{Player14}} Создаёт Хранителя как второго персонажа#Когда он умирает, создаёт синих пауков и навсегда убирает Соломенное чучело и любой предмет, который он подобрал, из инвентаря#{{DevilRoom}} Предметы из комнаты Дьявола стоят монет, пока Соломенное чучело живо#{{Warning}} Соломенное чучело может подбирать сюжетные предметы"}, -- Strawman
	[668] = {"668", "Папина записка", "Начинает Восхождение#Аксессуары, оставленные в предыдущих {{TreasureRoom}} комнатах сокровищ или {{BossRoom}} комнатах боссов, превращаются в {{Card78}} Треснувшие ключи"}, -- Dad's Note
	[669] = {"669", "Сосиска", "↑ {{Heart}} +1 к здоровью#↑ {{Speed}} +0.2 к скорости#↑ {{Tears}} +0.5 к скорострельности#↑ {{Damage}} +0.5 к урону#↑ {{Range}} +2.5 к дальности#↑ {{Shotspeed}} +0.16 к скорости полёта слёз#↑ {{Luck}} +1 к удаче#{{HealingRed}} Полное здоровье#↑ {{AngelDevilChance}} +6.9% шанс на комнату Дьявола/Ангела#↑ {{PlanetariumChance}} +6.9% шанс на Планетарий"}, -- Sausage
	[670] = {"670", "Варианты?", "Позволяет Исааку выбирать из двух разных наград за зачистку комнаты"}, -- Options?
	[671] = {"671", "Конфетное сердце", "↑ Лечение {{Heart}} красными сердцами даёт случайные постоянные повышения характеристик#{{Heart}} Создаёт красное сердце"}, -- Candy Heart
	[672] = {"672", "Фунт плоти", "{{DevilRoom}} Предметы из комнаты Дьявола стоят монет#{{Shop}} Предметы в магазинах стоят сердец#Расходуемые предметы в магазинах окружены шипами"}, -- A Pound of Flesh
	[673] = {"673", "Искупление", "{{DevilRoom}} Вход на новый этаж после посещения комнаты Дьявола и не взятия ни одного предмета/подбираемого предмета даёт:#↑ {{Damage}} +1 к урону#{{SoulHeart}} +1 сердце души"}, -- Redemption
	[674] = {"674", "Духовные оковы", "Получение смертельного урона превращает Исаака в призрака, прикованного к своему мёртвому телу, и позволяет ему продолжать сражаться с половиной сердца#Если призрак выживает, Исаак возрождается через 10 секунд#Должен перезаряжаться подбором сердца души"}, -- Spirit Shackles
	[675] = {"675", "Треснувшая сфера", "При получении урона:#Открывает все запертые двери в комнате#Показывает случайную комнату на карте#Разрушает все тонированные камни и камни с потайными комнатами"}, -- Cracked Orb
	[676] = {"676", "Пустое сердце", "{{EmptyHeart}} +1 пустой контейнер сердца при 1 красном сердце или меньше в начале нового этажа"}, -- Empty Heart
	[677] = {"677", "Астральная проекция", "{{Timer}} Получение урона в незачищенной комнате даёт на бой:#Призрачные слёзы#Полёт#Отрицает следующий урон#Останавливает время на 2 секунды#Значительно увеличивает скорость и скорострельность на 2 секунды"}, -- Astral Projection
	[678] = {"678", "Кесарево сечение", "{{Chargeable}} Заменяет слёзы Исаака на заряженную атаку, которая стреляет самонаводящимися, призрачными слезами-фецесами#{{Damage}} Слёзы-фецесы наносят около 2.8x урона Исаака в секунду"}, -- C Section
	[679] = {"679", "Маленький Абаддон", "{{Collectible399}} Фамильяр, который заряжается и выпускает круг Пасти Пустоты#Он наносит 52.5 урона за 1 секунду"}, -- Lil Abaddon
	[680] = {"680", "Месть Монтесумы", "{{Chargeable}} Стрельба заряжает короткодистанционный луч с высоким уроном назад#Не заменяет слёзы Исаака"}, -- Montezuma's Revenge
	[681] = {"681", "Маленький портал", "Наносит контактный урон и летит вперёд#Поглощает подбираемые предметы на своём пути#Каждый поглощённый подбираемый предмет увеличивает его размер, урон и создаёт синюю муху#Поглощение четырёх подбираемых предметов создаёт портал в неизведанную комнату"}, -- Lil Portal
	[682] = {"682", "Друг-червь", "Иногда вырывается из-под земли и хватает врага#Схваченные враги получают 8 урона в секунду, замедляются и не могут двигаться"}, -- Worm Friend
	[683] = {"683", "Костяные шпоры", "Враги создают костяные осколки после смерти#Кости блокируют снаряды и наносят контактный урон"}, -- Bone Spurs
	[684] = {"684", "Голодная душа", "Убийство врага имеет шанс создать призрака#Призраки преследуют врагов, наносят контактный урон и взрываются через 5 секунд#Исаак не получает урона от взрыва"}, -- Hungry Soul
	[685] = {"685", "Банка с огоньками", "Создаёт случайный огонёк#Создаёт один дополнительный огонёк при каждом использовании, до 12"}, -- Jar of Wisps
	[686] = {"686", "Медальон души", "↑ Подбор {{SoulHeart}} сердец души даёт случайные постоянные повышения характеристик#{{SoulHeart}} Создаёт сердце души"}, -- Soul Locket
	[687] = {"687", "Поисковик друзей", "{{Friendly}} Создаёт случайного дружелюбного монстра, который повторяет движения и атаки Исаака"}, -- Friend Finder
	[688] = {"688", "Внутренний ребёнок", "+1 жизнь#После смерти:#Возрождает Исаака в той же комнате с половиной сердца#↑ {{Speed}} +0.2 к скорости#↑ Значительное уменьшение размера"}, -- Inner Child
	[689] = {"689", "Глючная корона", "Пьедесталы с предметами быстро циклически меняются между 5 случайными предметами"}, -- Glitched Crown
	[690] = {"690", "Желе-живот", "Враги отскакивают от Исаака#50% шанс отрицать контактный урон#50% шанс отразить вражеские снаряды"}, -- Belly Jelly
	[691] = {"691", "Священная сфера", "Предотвращает появление предметов качества {{Quality0}}/{{Quality1}}#Предметы качества {{Quality2}} имеют 33% шанс быть переброшенными"}, -- Sacred Orb
	[692] = {"692", "Кровная связь", "Создаёт набор шипов в {{DevilRoom}} комнате Дьявола#Получение урона на шипах даёт:#35%: Ничего#33%: ↑ {{Damage}} +0.5 к урону#15%: 6 {{Coin}} пенни#10%: 2 {{BlackHeart}} чёрных сердца#5%: {{DevilRoom}} Случайный предмет Дьявола#2%: Превращение в Левиафана"}, -- Sanguine Bond
	[693] = {"693", "Рой", "Даёт 8 орбитальных мух#Зачистка комнаты создаёт новую муху#Мухи превращаются в синих мух после блокировки выстрела"}, -- The Swarm
	[694] = {"694", "Разбитое сердце", "↑ {{Damage}} +0.25 к урону за каждое разбитое сердце#{{BrokenHeart}} +3 разбитых сердца#{{BrokenHeart}} Каждый смертельный удар даёт +2 разбитых сердца#Исаак умирает при 12 разбитых сердцах"}, -- Heartbreak
	[695] = {"695", "Кровавый порыв", "При получении урона даёт на этаж:#↑ {{Speed}} Увеличение скорости#↑ {{Tears}} Увеличение скорострельности#Максимум +1.02 к скорости и +3 к скорострельности"}, -- Bloody Gust
	[696] = {"696", "Спасение", "Исаак окружён нимбом#Враги, которые стоят в нимбе слишком долго, поражаются крестообразным лучом света#Получение урона увеличивает размер нимба на этаж"}, -- Salvation
	[697] = {"697", "Исчезающий близнец", "Вход в комнату босса создаёт клона босса#Победа над клоном создаёт дополнительный предмет#Клон медленнее и имеет 75% здоровья"}, -- Vanishing Twin
	[698] = {"698", "Скрученная пара", "Два фамильяра, которые стреляют слезами с теми же характеристиками и эффектами, что и у Исаака#{{Damage}} Они наносят 37.5% урона Исаака"}, -- Twisted Pair
	[699] = {"699", "Ярость Азазеля", "{{Collectible118}} Зачистка 4 комнат выстреливает большим лучом Бримстоуна при входе в следующую комнату"}, -- Azazel's Rage
	[700] = {"700", "Эхо-камера", "Использование {{Rune}} руны, {{Card}} карты или {{Pill}} пилюли также использует копию последних 3 использованных рун/карт/пилюль после подбора Эхо-камеры"}, -- Echo Chamber
	[701] = {"701", "Гробница Исаака", "Создаёт {{DirtyChest}} Старый сундук в начале каждого этажа#Старые сундуки требуют ключ для открытия и могут содержать {{SoulHeart}} сердца души, {{Trinket}} аксессуары или предметы Мамы, Папы и Ангела"}, -- Isaac's Tomb
	[702] = {"702", "Мстительный дух", "Получение урона создаёт орбитальный огонёк#Огоньки стреляют слезами, не блокируют выстрелы и исчезают на следующем этаже#Максимум 6 огоньков"}, -- Vengeful Spirit
	[703] = {"703", "Исав-младший", "Переключается между текущим персонажем и Исавом-младшим#Исав-младший имеет {{BlackHeart}} 3 чёрных сердца, {{Damage}} +2 к урону, полёт и случайные предметы, равные количеству предметов у игрока при первом использовании этого предмета#Персонажи имеют независимые предметы и здоровье#{{Warning}} Смерть любого персонажа заканчивает забег"}, -- Esau Jr.
	[704] = {"704", "Берсерк!", "{{Battery}} Заряжается нанесённым уроном#{{Timer}} Даёт на 5 секунд:#↑ {{Speed}} +0.4 к скорости#↓ {{Tears}} x0.5 множитель скорострельности#↑ {{Tears}} +2 к скорострельности#↑ {{Damage}} +3 к урону#Ограничивает атаки до ближнего боя, который отражает выстрелы#{{Timer}} Каждое убийство увеличивает длительность на 1 секунду и даёт кратковременную неуязвимость"}, -- Berserk!
	[705] = {"705", "Тёмные искусства", "{{Timer}} Даёт на 1 секунду (или до стрельбы):#↑ {{Speed}} +1 к скорости#Исаак может проходить сквозь врагов/снаряды и парализует их#Когда эффект заканчивается, наносит урон парализованным врагам, убирает парализованные снаряды и создаёт взрыв в местоположении Исаака#Атаки и взрыв мощнее, чем больше врагов/снарядов было поражено"}, -- Dark Arts
	[706] = {"706", "Бездна", "Поглощает все пьедесталы с предметами в комнате и создаёт саранчу-фамильяра за каждый из них#Саранча наносит урон Исаака 2-3 раза за атаку#Некоторые предметы создают особую саранчу при поглощении"}, -- Abyss
	[707] = {"707", "Лёгкий ужин", "↑ {{Heart}} +1 к здоровью#{{HealingRed}} Лечит 1 сердце"}, -- Supper
	[708] = {"708", "Степлер", "↑ {{Damage}} +1 к урону#Все слёзы Исаака выстреливаются из правого глаза"}, -- Stapler
	[709] = {"709", "Суплекс!", "Исаак делает рывок в направлении, в котором движется#Рывок во врага или босса подхватывает его и бросает на землю#Бросок наносит урон и создаёт каменные волны в зависимости от размера Исаака#Вы неуязвимы во время рывка и броска"}, -- Suplex!
	[710] = {"710", "Сумка для крафта", "Собирает до 8 подбираемых предметов, которые нельзя выбросить#Использование предмета с 8 подбираемыми предметами в сумке создаёт предмет#Качество предмета зависит от качества подбираемых предметов"}, -- Bag of Crafting
	[711] = {"711", "Переворот", "Вход в комнату с пьедесталами с предметами показывает призрачный второй предмет на пьедесталах#Использование предмета меняет местами реальный и призрачный предмет#Использование Переворота после взятия первого предмета позволяет Исааку подобрать другой предмет#{{Warning}} Призрачные предметы на пьедесталах исчезают после выхода из комнаты"}, -- Flip
	[712] = {"712", "Лемегетон", "Создаёт орбитальный спутник, который даёт эффект случайного предмета#Предметы имеют 25% шанс быть из пула предметов текущей комнаты и 75% шанс быть из пулов Сокровищ, Боссов или Магазина"}, -- Lemegeton
	[713] = {"713", "Сумпториум", "Убирает половину сердца и создаёт сгусток#Сгустки копируют слёзы Исаака#Каждый тип сердца создаёт сгусток с разным здоровьем, уроном и эффектом слезы"}, -- Sumptorium
	[714] = {"714", "Возврат", "Возвращает тело Забытого с любого расстояния#Душа неуязвима, пока Забытый возвращается"}, -- Recall
	[715] = {"715", "Задержка", "Использование предмета, когда он пуст, сохраняет следующую какашку внутри#Использование предмета с какашкой внутри использует эту какашку"}, -- Hold
	[716] = {"716", "Мешок Хранителя", "Создаёт 3 {{Coin}} монеты и 1 {{Key}} ключ#{{Shop}} Потратив 3 монеты, вы получаете одно из следующих улучшений:#↑ {{Speed}} +0.03 к скорости#↑ {{Damage}} +0.5 к урона#↑ {{Range}} +0.25 к дальности"}, -- Keeper's Sack
	[717] = {"717", "Родня Хранителя", "Камни и другие препятствия создают 2 синих пауков при уничтожении#Камни могут иногда создавать синих пауков во вражеских комнатах"}, -- Keeper's Kin
	[718] = {"718", "", "<Предмет не существует>"},
	[719] = {"719", "Ящик Хранителя", "{{Shop}} Создает случайный предмет/подбираемый предмет из магазина для покупки"}, -- Keeper's Box
	[720] = {"720", "Банка со всем", "Создает подбираемые предметы в зависимости от количества зарядов#Награды за заряд:#{{Blank}} 1:{{PoopPickup}} 2:{{Coin}} 3:{{Bomb}} 4:{{Key}}#{{Blank}} 5:{{Heart}} 6:{{Pill}} 7:{{Card}} 8:{{SoulHeart}}#{{Blank}} 9:{{GoldenHeart}} 10:{{GoldenKey}} 11:{{GoldenBomb}}#{{Blank}} При 12 зарядах активирует мощный случайный эффект"}, -- Everything Jar
