	[28] = {"Испорченный Азазель", "Когда ты начинаешь заряжаться, ты чихаешь кровью#Попадание во врага чихом сокращает время зарядки вдвое#Чих наносит 1.5x урона Азазеля#{{BrimstoneCurse}} Пораженные враги получают дополнительный урон от лучей Серы#При смерти проклятые враги взрываются и передают проклятие ближайшим врагам"},
	[29] = {"Испорченный Лазарь", "У Лазаря есть два состояния, каждое со своими предметами и здоровьем#Зачистка комнаты/волны или использование Переворота переключает на другое состояние"},
	[30] = {"Испорченный Эдем", "При получении урона перероллирует твои характеристики, предметы, брелок и расходуемые предметы#Предметы перероллируются в предмет из того же пула предметов#Самоурон не перероллирует"},
	[31] = {"Испорченный Потерянный", "{{Card51}} Карты, которые появляются, имеют 10% шанс быть Святой Картой#Предметы Качества {{Quality2}} или ниже имеют 20% шанс быть перероллены#Могут появляться только \"наступательные\" предметы"},
	[32] = {"Испорченная Лилит", "Нажатие Огня запускает короткобойную рукопашную атаку плода, наносящую 3x урона#Удержание Огня удерживает плод, стреляющий слезами в ближайшего врага"},
	[33] = {"Испорченный Хранитель", "Максимум 2 Монетных Сердца#Враги роняют монеты, которые исчезают через 2 секунды#Большинство постаментов предметов стоят 15 монет#Дьявольские сделки и предметы Ангела стоят 15 или 30 монет#Магазины не требуют ключа и имеют увеличенный ассортимент"},
	[34] = {"Испорченный Аполлион", ""},
	[35] = {"Испорченный Забытый", "Забытый - это неподвижная куча костей, которую Душа подбирает и бросает, нанося 3x урона#Только Душа может получать урон#Бомбы размещаются в месте Забытого#Не может иметь Красных Сердец#{{SoulHeart}} Увеличение здоровья дарует Сердца Души"},
	[36] = {"Испорченная Бетани", "{{Heart}} Использует Красные Сердца для зарядки активного предмета#Не может иметь Красных Сердец#{{SoulHeart}} Увеличение здоровья дарует Сердца Души и заряды крови#Увеличение характеристик эффективно только на 75%"},
	[37] = {"Испорченный Иаков", "Темный Исав преследует тебя, бросаясь на тебя, когда ты близко#Рывок наносит много урона врагам#Если он попадет в тебя, ты превращаешься в призрака, который умирает с одного удара до конца этажа#Будучи призраком, одну дьявольскую сделку за комнату можно взять бесплатно"},
	[38] = {"Мертвый Испорченный Лазарь", "У Лазаря есть два состояния, каждое со своими предметами и здоровьем#Зачистка комнаты/волны или использование Переворота переключает на другое состояние"},
	[39] = {"Душа Испорченного Иакова", "Полет#Призрачные слезы#{{Warning}} Нет здоровья#{{Warning}} Умираешь, если тебя ударит Темный Исав#{{DevilRoom}} Одну дьявольскую сделку за комнату можно взять бесплатно#Превращаешься обратно в Испорченного Иакова на следующем этаже"},
	[40] = {"Душа Испорченного Забытого", "Забытый - это неподвижная куча костей, которую Душа подбирает и бросает, нанося 3x урона#Только Душа может получать урон#Бомбы размещаются в месте Забытого#Не может иметь Красных Сердец#{{SoulHeart}} Увеличение здоровья дарует Сердца Души"},
}
